  constructor(props) {
    super(props);
    this.state = {
      country: 'us', // Default country
      searchTerm: '', // State for search input
    };
  }

  // Handler for country selection change
  handleCountryChange = (event) => {
    this.setState({ country: event.target.value });
  };

